import { useState } from "react";
import { StringParam, NumberParam, useQueryParam } from 'use-query-params';


import "./../../App.css";

export default function Seat(props) {
  const [selectedSeat, setSelectedSeat] = useState(false);
  const [seatId, setSeatId] = useState();
  //const [seatId, setSeatId] = useQueryParam("seat-number", NumberParam);


  const handleSeatSelect = (number) => {
   setSelectedSeat(!selectedSeat);
   setSeatId(number*10/ Math.random)

 }

  return (
    <section className="row-seats">
      <span
        className={`${selectedSeat ? "selected-seat" : "seat"}`}
        onClick={()=> handleSeatSelect(props.number)}
      >
        {props.number}
      </span>
      <span>{seatId}</span>
    </section>
  );
}
