import { useState } from "react";
import { StringParam, NumberParam,BooleanParam, useQueryParam } from 'use-query-params';

import "./../../App.css";

export default function Seat(props) {
  const [selectedSeat, setSelectedSeat] = useQueryParam('selectedTicket-f', BooleanParam);

  return (
    <section className="row-seats">
      <span
        className={`${props.selectedSeat ? "selected-seat" : "seat"}`}
        onClick={()=>setSelectedSeat(!selectedSeat)}
      >
        {props.number}
      </span>
    </section>
  );
}
