import Seat from "../Seat/Seat";
import { rows, columns, movies } from "./../../constants";
import "./../../App.css";

export default function MovieSeats(props) {
  const rowValue = movies.filter(movies => movies.id === 1).map((movie)=>movie.rows);
  const columnValue = movies.filter(movies => movies.id === 1).map((movie)=>movie.cols);

  return (
    <section className="movie-seat-container">
      <h3>{props.movie}</h3>
      {columnValue.map((columns) => (
        <section className="movie-seats">
          <span>{columns}</span>
          <section className="rows">
            {rowValue.map((rows) => (
              <Seat number={rows} />
            ))}
          </section>
        </section>
      ))}
    </section>
  );
}
